import React, {FormEvent, useEffect, useState} from "react";
import { useHistory } from "react-router-dom";
import { FiArrowLeft } from "react-icons/fi";

import CadloginImg from '../images/fitness_homemMulher.png';
import api from '../services/api';

import '../styles/pages/cadastrarLogin.css';

interface Alunos {
  id: string;
  nome:string;
  idade:string;
  sexo:string;
  peso:string;
  altura:string;
  cintura:string;
  sessoes:string;
  personal_id:string;
}

function CadastrarAluno() {

    let achou = false;

    const history = useHistory();
    const queryParams = new URLSearchParams(history.location.search);
  
    //const [alunos,setAlunos] = useState<Alunos[]>([]);

    useEffect(()=> {
    //api.get('alunos').then(response => {
    //  setAlunos(response.data);
    //  });
        });

        const [id,  setId]   = useState('');
        const [nome, setNome]   = useState('');
        const [idade, setIdade] = useState('');
        const [sexo, setSexo] = useState('');
        const [peso, setPeso] = useState('');
        const [altura, setAltura] = useState('');
        const [cintura, setCintura] = useState('');
        const [sessoes, setSessoes] = useState('');
        const [personal_id, setPersonalId] = useState('');

        const alunos = {id,nome,idade,sexo,peso,altura,cintura,sessoes,personal_id}
        //const [alunos,setAlunos] = useState<Alunos>();  
        api.get('alunos/'+queryParams.get('id')).then(resposta => {
          const {
            id, 
            nome,
            idade,
            sexo, 
            peso, 
            altura,
            cintura,
            sessoes,
            personal_id,
    
          } = resposta.data}); 
        //const alunos = resposta.data;

        //const [nome, setNome]   = useState(alunos.nome);
        //const [idade, setIdade] = useState(alunos.idade);
        //const [sexo, setSexo] = useState(alunos.sexo);
        //const [peso, setPeso] = useState(alunos.peso);
        //const [altura, setAltura] = useState(alunos.altura);
        //const [cintura, setCintura] = useState(alunos.cintura);
        //const [sessoes, setSessoes] = useState(alunos.sessoes);
        //const [personal_id, setPersonalId] = useState(alunos.personal_id);
        
        api.get('personals/'+personal_id).then(responsePersonal => {
          const {
            id, 
            nome,
            banco_id,
            agencia,
            conta,    
          } = responsePersonal.data}); 
        const personal = {id,nome,banco_id,agencia,conta};
        const [personalNome,setPersonalNome] = useState(personal.nome);

    async function handleSubmit(event: FormEvent){
      event.preventDefault();

    
      //await api.post('logins',login);
      //alert('cadastro realizado com suesso');
        
  }; 

   const { goBack } = useHistory();

    return (
        <div id="page-cadastrarLogin">
            <aside>
               <header>
                   <img src={CadloginImg} alt="login" />
                   <h2>Cadastrar login dos alunos</h2>
                   <p>Sua saúde em primeiro lugar :)</p>
               </header> 
               <footer>
                    <strong>Direito de cópia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form onSubmit={handleSubmit} className="create-cadlogin-form">
                    <div className="input-block">
                      <label htmlFor="name">Nome</label>
                      <input id="nome" value={nome} onChange={event =>setNome(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Idade</label>
                      <input id="idade" value={idade} onChange={event =>setIdade(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Sexo</label>
                      <input id="sexo" value={sexo} onChange={event =>setSexo(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Peso</label>
                      <input id="peso" value={peso} onChange={event =>setPeso(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Altura</label>
                      <input id="altura" value={altura} onChange={event =>setAltura(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Cintura</label>
                      <input id="Cintura" value={cintura} onChange={event =>setCintura(event.target.value)}/>
                    </div>
                    <div className="radio">
          <label>
            <input
              type="radio"
              value="2"
            />
            Duas
          </label>
        </div>
        <div className="radio">
          <label>
            <input
              type="radio"
              value="3"
            />
            Três
          </label>
        </div>
        <div className="radio">
          <label>
            <input
              type="radio"
              value="4"
            />
            Quatro
          </label>
        </div>                    
        <div className="radio">
          <label>
            <input
              type="radio"
              value="5"
            />
            Cinco
          </label>
        </div>                    
                    <div className="input-block">
                      <label htmlFor="name">Personal</label>
                      <input id="personal" value={personalNome} onChange={event =>setPersonalNome(event.target.value)} readOnly={true}/>
                    </div>

                    <button className="confirm-button" type="submit">
                            Confirmar
                    </button>

                </form>
            </main>

        </div>
    )
}

export default CadastrarAluno;
