import React, {FormEvent, useEffect, useState, useRef} from "react";
import { useHistory,useLocation } from "react-router-dom";
import { FiArrowLeft, FiPlus } from "react-icons/fi";

import CadloginImg from '../images/personal.png';
import api from '../services/api';

import '../styles/pages/cadastrarLogin.css';

interface Personals {
  id: string | null;
  nome: string | null;
  banco_id: string | null; 
}

interface cadastrarLogins {
  nome: string;  
  email: string;
  senha: string;
  cadastro_id:string;
}


function CadastrarLogin() {

    let achou = false;
    let personalNome = '';

    const history = useHistory();
    const queryParams = new URLSearchParams(history.location.search);
    
  
//    const [cadastrarlogins, setCadastrarLogins] = useState<cadastrarLogins[]>([]); 

  
    //const personalsIni : Personals = {id:null,nome:null,banco_id:null}; 
    //const [personals,setPersonals] = useState<Personals>(personalsIni);

    //const personalsIni = useRef<Personals>(null);
    //const [personals,setPersonals] = useState<Personals | null>();

    const [personals,setPersonals] = useState<Personals[]>([]);
    //const [personals,setPersonals] = useState<Personals>();
    
    useEffect(()=> {
    api.get('personals/').then(response => {
      setPersonals(response.data);
      });
        }, []);

        personals.forEach(psl =>{
          if (psl.id===queryParams.get('id')) {
                achou = true;
                personalNome = psl.nome;
          };
          });
    
    const [nome, setNome]   = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    //const [cadastro_id,setCadastroId] = useState('');
    //const [cadastro_id, setCadastroId] = useState(personals?.nome);
    //const [cadastro_id, setCadastroId] = useState(queryParams.get('id')+'');


    function handleSubmit(event: FormEvent){
      event.preventDefault();
      //console.log(event);
  }; 

   const { goBack } = useHistory();

    return (
        <div id="page-cadastrarLogin">
            <aside>
               <header>
                   <img src={CadloginImg} alt="login" />
                   <h2>Cadastrar login dos alunos</h2>
                   <p>Sua saúde em primeiro lugar :)</p>
               </header> 
               <footer>
                    <strong>Direito de cópia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form onSubmit={handleSubmit} className="create-cadlogin-form">
                    <div className="input-block">
                      <label htmlFor="name">Nome</label>
                      <input id="nome" value={nome} onChange={event =>setNome(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Email</label>
                      <input id="email" value={email} onChange={event =>setEmail(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Senha</label>
                      <input id="senha" value={senha} onChange={event =>setSenha(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Personal</label>
                      <input id="personal" value={personalNome} />
                    </div>

                    <button className="confirm-button" type="submit">
                            Confirmar
                    </button>

                </form>
            </main>

        </div>
    )
}

export default CadastrarLogin;
