import React, {FormEvent, useEffect, useState} from "react";
import { useHistory, useParams } from "react-router-dom";
import { FiArrowLeft, FiPlus } from "react-icons/fi";
import {Link} from 'react-router-dom';
import ReactDataGrid from 'react-data-grid';



import loginImg from '../images/decatlo.png';
import api from '../services/api';

import '../styles/pages/treino.css';
import { StringifyOptions } from "querystring";
import FlatButton from "material-ui/FlatButton";


interface Aluno  {
    id:number;
    nome:string;
    idade:number;
    sexo:string;
    peso:number;
    altura:number;
    cintura:number;
    sessoes:number;
}

interface Personal  {
  id:string;
  nome:string;
  banco_id:string;
  alunos:Array<{
    id:number;
    nome:string;
    idade:number;
    sexo:string;
    peso:number;
    altura:number;
    cintura:number;
    sessoes:number;
  }>;
}


function Treino() {

    //const params = useParams<PersonalParams>()

    const history = useHistory();
    const params = new URLSearchParams(history.location.search);

    const [personal, setPersonal] = useState<Personal>();
    const [rows,setRows] = useState<Aluno[]>([]);
 
      //api.get(`personals/${params.get('id')}`).then(response => {
      //  setPersonal(response.data);
      //});
      //const DataFormatter = ({ id }) => { return <Link to={{pathname:"/cadastraraluno", search: '?id='+params.get('id'), state: { detail: params.get('id') }}}>
      //{ id }</Link> };

      //<Button variant="contained" color="primary" startIcon={<EditIcon />}>
      //Edit
    //</Button>


      
      const columns = [
        { key: "id", name: "ID", editable: false},
        { key: "nome", name: "NOME", editable: false },
        { key: "idade", name: "IDADE", editable: false },
        { key: "sexo", name : "SEXO", editable: false },
        { key: "peso", name: "PESO", editable: false},
        { key: "altura", name:"ALTURA", editable:false},
        { key: "cintura", name:"CINTURA", editable:false},
        { key: "sessoes", name:"SESSOES", editable: false},
        { key: "link", name:"LINK", disableClickEventBubbling: true,
        renderCell: () => {
          return (
            <Link to={{pathname:"/cadastraraluno", search: '?id='+params.get('id'), state: { detail: params.get('id') }}}>
              <button className="confirm-button" type="button">Treino</button>
            </Link>
          );}}
        
      ];

    useEffect(()=> {
      api.get('personals/'+params.get('id')).then(response => {
        setPersonal(response.data);
        if (personal){
          setRows(personal.alunos);
        }
      });

    }, [params.get('id')]);

    const MyFormatter = ({
      render(){
        return(
          <Link to={{pathname:"/cadastraraluno", search: '?id='+params.get('id'), state: { detail: params.get('id') }}}></Link>
        );
      }
    });
    
    
    function MyFunctionalDataTable(){
      const myCols = [
        { key: "id", name: "ID", editable: false},
        { key: "nome", name: "NOME", editable: false },
        { key: "idade", name: "IDADE", editable: false },
        { key: "sexo", name : "SEXO", editable: false },
        { key: "peso", name: "PESO", editable: false},
        { key: "altura", name:"ALTURA", editable:false},
        { key: "cintura", name:"CINTURA", editable:false},
        { key: "sessoes", name:"SESSOES", editable: false},
        { key: "link", name: "Link", width: 200, formatter: MyFormatter, getRowMetaData: (row)=>row},
       ];
      if (personal) {
        const createRowData = !personal.alunos? '' :
        personal.alunos.map(function(obj, index){
          return {"id": obj.id,
                  "nome": obj.nome, //doesn't have to match a GUI column
                  "idade": obj.idade, //doesn't have to match a GUI column 
                  "sexo": obj.sexo,
                  "peso":obj.peso,
                  "altura":obj.altura,
                  "cintura":obj.cintura,
                  "sessoes":obj.sessoes,
          }
        });
        const rowGetter =  createRowData.length;
      
        return (
          <ReactDataGrid
            columns={myCols}
            rowGetter={rowGetter}
            rowsCount={createRowData.length}
            headerRowHeight={35}
            rowHeight={35}
            minHeight={createRowData.length<15? (37+(35*createRowData.length)) : 500}
          />
      
        )
      }
    
    }


    const { goBack } = useHistory();


    return (
        <div id="page-personal">
            <aside>
               <header>
                   <img src={loginImg} alt="personal" />
                   <h2>Programar treino</h2>
                   <h2>Cadastrar login de aluno</h2>
               </header> 
               <footer>
                    <strong>Direito de c√≥pia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form  className="create-personal-form">
                  {MyFunctionalDataTable()}
                </form>
            </main>

        </div>
    )
}

export default Treino;
