import React, {FormEvent, useEffect, useState} from "react";
import { useHistory } from "react-router-dom";
import { FiArrowLeft, FiPlus } from "react-icons/fi";
import {Link} from 'react-router-dom';
import ReactDataGrid from 'react-data-grid';



import loginImg from '../images/decatlo.png';
import api from '../services/api';

import '../styles/pages/treino.css';

interface Personal  {
  id:string;
  nome:string;
  banco_id:string;
  alunos:Array<{
    id:number;
    nome:string;
    idade:number;
    sexo:string;
    peso:number;
    altura:number;
    cintura:number;
    sessoes:number;
  }>;
}


function Treino() {

    const history = useHistory();
    const queryParams = new URLSearchParams(history.location.search);

    const [aluno, setPersonal] = useState<Personal[]>([]);


    useEffect(()=> {
      loadPersonal();
    }, [queryParams.get('id')]);

    async function loadPersonal() {
      const response = await api.get('personal/'+queryParams.get('id'));
        setPersonal(response.data);
    }


    const { goBack } = useHistory();

    const columns = [
      { key: "id", name: "ID", editable: true },
      { key: "title", name: "Title", editable: true },
      { key: "complete", name: "Complete", editable: true }
    ];
    
    const rows = [
      { id: 0, title: "Task 1", complete: 20 },
      { id: 1, title: "Task 2", complete: 40 },
      { id: 2, title: "Task 3", complete: 60 }
    ];

    return (
        <div id="page-personal">
            <aside>
               <header>
                   <img src={loginImg} alt="personal" />
                   <h2>Programar treino</h2>
                   <h2>Cadastrar login de aluno</h2>
               </header> 
               <footer>
                    <strong>Direito de c√≥pia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form  className="create-personal-form">
                <ReactDataGrid 
                    columns={columns} rows={rows} />

                </form>
            </main>

        </div>
    )
}

export default Treino;
