import React, {FormEvent, useEffect, useState} from "react";
import { useHistory } from "react-router-dom";
import { FiArrowLeft, FiPlus } from "react-icons/fi";

import loginImg from '../images/cadeado.png';
import api from '../services/api';

import '../styles/pages/login.css';


interface Login {
  email: string;
  senha: string;
}


function Login() {

    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [logins, setLogins] = useState<Login[]>([]);

    useEffect(()=> {
      api.get('logins').then(response => {
        setLogins(response.data);
      });
    }, []);

    function handleSubmit(event: FormEvent){
      event.preventDefault();

      logins.forEach(login =>{
          {login.email=email}
      });

      console.log({email,senha});
  } 

    const { goBack } = useHistory();

    return (
        <div id="page-login">
            <aside>
               <header>
                   <img src={loginImg} alt="login" />
                   <h2>Efetuar login no sistema</h2>
                   <p>Sua saúde em primeiro lugar :)</p>
               </header> 
               <footer>
                    <strong>Direito de cópia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form onSubmit={handleSubmit} className="create-login-form">
                    <div className="input-block">
                      <label htmlFor="name">Email</label>
                      <input id="email" value={email} onChange={event =>setEmail(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Senha</label>
                      <input id="senha" value={senha} onChange={event =>setSenha(event.target.value)}/>
                    </div>
                    <button className="confirm-button" type="submit">
                            Confirmar
                    </button>

                </form>
            </main>

        </div>
    )
}

export default Login;
