import React, {FormEvent, useEffect, useState} from "react";
import { useHistory,useLocation } from "react-router-dom";
import { FiArrowLeft, FiPlus } from "react-icons/fi";

import CadloginImg from '../images/personal.png';
import api from '../services/api';

import '../styles/pages/cadastrarLogin.css';


interface cadastrarLogins {
  nome: string;  
  email: string;
  senha: string;
}


function CadastrarLogin() {

    const history = useHistory();
    const [nome, setNome]   = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [cadastrarlogins, setCadastrarLogins] = useState<cadastrarLogins[]>([]);

    const SecondPage = props => {
      const location = useLocation();
  
      useEffect(() => {
         console.log(location.pathname); // result: '/secondpage'
         console.log(location.search); // result: '?query=abc'
         console.log(location.state); // result: 'some_value'
      }, [location]);
  
  };
    let id = useLocation().state;

    //useEffect(()=> {
    //  api.get('logins').then(response => {
    //    setCadastrarLogins(response.data);
    //  });
    //}, []);

    function handleSubmit(event: FormEvent){
      event.preventDefault();
      console.log(event);

    //  logins.forEach(lgn =>{
    //      if (lgn.email===email) {
    //        if (lgn.senha===senha){
    //            achou = true;
    //        } 
    //      };
    //  });
  //    {achou ? history.push('/'):alert('usuário não encontrado')};
  }; 

   const { goBack } = useHistory();

    return (
        <div id="page-cadastrarLogin">
            <aside>
               <header>
                   <img src={CadloginImg} alt="login" />
                   <h2>Cadastrar login dos alunos</h2>
                   <p>Sua saúde em primeiro lugar :)</p>
               </header> 
               <footer>
                    <strong>Direito de cópia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form onSubmit={handleSubmit} className="create-cadlogin-form">
                    <div className="input-block">
                      <label htmlFor="name">Nome</label>
                      <input id="nome" value={nome} onChange={event =>setNome(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Email</label>
                      <input id="email" value={email} onChange={event =>setEmail(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Senha</label>
                      <input id="senha" value={senha} onChange={event =>setSenha(event.target.value)}/>
                    </div>
                    <div className="input-block">
                      <label htmlFor="name">Personal</label>
                      <input id="personal" value={senha} onChange={event =>setSenha(event.target.value)}/>
                    </div>
                    <button className="confirm-button" type="submit">
                            Confirmar
                    </button>

                </form>
            </main>

        </div>
    )
}

export default CadastrarLogin;
