import React, {FormEvent, useEffect, useState} from "react";
import { useHistory } from "react-router-dom";
import { FiArrowLeft, FiPlus } from "react-icons/fi";
import {Link} from 'react-router-dom';
import ReactDataGrid from 'react-data-grid';



import loginImg from '../images/decatlo.png';
import api from '../services/api';

import '../styles/pages/treino.css';

interface Personal  {
  id:string;
  nome:string;
  banco_id:string;
  alunos:Array<{
    id:number;
    nome:string;
    idade:number;
    sexo:string;
    peso:number;
    altura:number;
    cintura:number;
    sessoes:number;
  }>;
}


function Treino() {

    const history = useHistory();
    const queryParams = new URLSearchParams(history.location.search);

    const [personal, setPersonal] = useState<Personal>();


    useEffect(()=> {
      loadPersonal();
    }, [queryParams.get('id')]);

    async function loadPersonal() {
      const response = await api.get('personals/'+queryParams.get('id'));
        setPersonal(response.data);
    }

    console.log(personal);

    const { goBack } = useHistory();

    const columns = [
      { key: "id", name: "ID", editable: false },
      { key: "nome", name: "NOME", editable: false },
      { key: "idade", name: "IDADE", editable: false },
      { key: "sexo", name : "SEXO", editable: false },
      { key: "peso", name: "PESO", editable: false},
      { key: "altura", name:"ALTURA", editable:false},
      { key: "cintura", name:"CINTURA", editable:false}
    ];
    
    const rows = [
      { id: 0, nome: "Task 1", idade: 20, sexo: "M", peso: 20, altura: 30, cintura: 40 },
      { id: 1, nome: "Task 1", idade: 20, sexo: "M", peso: 20, altura: 30, cintura: 40 },
      { id: 2, nome: "Task 1", idade: 20, sexo: "M", peso: 20, altura: 30, cintura: 40 },
      { id: 3, nome: "Task 1", idade: 20, sexo: "M", peso: 20, altura: 30, cintura: 40 },
      { id: 4, nome: "Task 1", idade: 20, sexo: "M", peso: 20, altura: 30, cintura: 40 }
    ];

    return (
        <div id="page-personal">
            <aside>
               <header>
                   <img src={loginImg} alt="personal" />
                   <h2>Programar treino</h2>
                   <h2>Cadastrar login de aluno</h2>
               </header> 
               <footer>
                    <strong>Direito de cópia</strong>
                    <span>SSA Sistemas</span>
                      <button type="button" onClick={goBack}>
                        <FiArrowLeft size={24} color="#FFF" />
                      </button>
               </footer>
            </aside>
            <main>
                <form  className="create-personal-form">
                <ReactDataGrid 
                    columns={columns} rows={rows} />

                </form>
            </main>

        </div>
    )
}

export default Treino;
